mapper.select:
select c.id, c.ext_corp_id, m.ext_staff_id as ext_creator_id, c.ext_id, c.name, c.position, c.corp_name,
c.avatar,
c.type, c.gender, c.unionid, c.external_profile, m.create_time as created_at, c.updated_at, c.has_delete,
m.is_deleted_staff from wx_customer c
left join wx_customer_staff m on c.id = m.customer_id
left join wx_staff s on m.ext_corp_id = s.ext_corp_id and binary m.ext_staff_id = s.ext_id
left join wx_customer_staff_tag t on m.ext_customer_id = t.ext_customer_id and t.ext_staff_id = m.ext_staff_id
and t.has_delete is not null

    m.has_delete is not null and c.has_delete is not null and s.has_delete is not null
    and c.has_friend = 1

        and c.ext_corp_id =#{dto.extCorpId}


        and c.id not in

            #{noCustomerId}



        and c.gender =#{dto.gender}


        and c.type =#{dto.type}


        and c.name like concat('%',#{dto.name},'%')


        and s.id in

            #{staffId}



        and m.ext_staff_id in

            #{item}



        and m.ext_staff_id = #{dto.loginStaffExtId}


        and t.ext_tag_id in

            #{item}



        and m.create_time >= #{dto.createdAtBegin}


        and m.create_time <= #{dto.createdAtEnd}


group by m.id
order by IF(ISNULL(c.updated_at),1,0), m.create_time desc, m.id desc

select *
from wx_customer
where ext_corp_id = #{extCorpId}
  and ext_id = #{extId}
  and has_friend = 1
order by has_delete is null, created_at desc limit 1

select * from wx_customer
where ext_corp_id =#{extCorpId}
and ext_id in

    #{item}

and has_friend = 1

select c.id, c.ext_corp_id, c.ext_creator_id, c.ext_id, c.name, c.position, c.corp_name, c.avatar, c.type, c.gender, c.unionid, c.external_profile, c.created_at, c.updated_at, c.has_delete, c.is_deleted_staff from wx_customer c left join wx_customer_staff_tag t on c.ext_id = t.ext_customer_id and t.has_delete is not null left join br_journey_stage_customer s on s.ext_corp_id = c.ext_corp_id and s.customer_ext_id = c.ext_id and s.journey_id = #{dto.journeyId} and s.deleted_at is null
c.has_delete is not null and s.id is null and c.has_friend = 1
and c.ext_corp_id =#{dto.extCorpId}
and c.name like concat('%',#{dto.name},'%')
and t.ext_tag_id in
#{item}
and c.created_at >= #{dto.createdAtBegin}
and c.created_at <= #{dto.createdAtEnd}
group by c.id order by IF(ISNULL(c.updated_at),1,0), c.updated_at desc

select c.id, c.ext_corp_id, m.ext_staff_id as ext_creator_id, c.ext_id, c.name, c.position, c.corp_name,
c.avatar,
c.type, c.gender, c.unionid, c.external_profile, m.create_time as created_at, c.updated_at, c.has_delete,
m.is_deleted_staff from wx_customer c
left join wx_customer_staff m on c.id = m.customer_id
left join wx_staff s on m.ext_corp_id = s.ext_corp_id and binary m.ext_staff_id = s.ext_id

    m.has_delete is not null and c.has_delete is not null and s.has_delete is not null
    and c.has_friend = 1

        and c.ext_corp_id =#{dto.extCorpId}


        and c.gender =#{dto.gender}


        and c.type =#{dto.type}


        and c.name like concat('%',#{dto.name},'%')


        and c.created_at >= #{dto.createdAtBegin}


        and c.created_at <= #{dto.createdAtEnd}


order by IF(ISNULL(c.updated_at),1,0), c.updated_at desc

select c.id, c.ext_corp_id, c.ext_creator_id, c.ext_id, c.name, c.position, c.corp_name, c.avatar, c.type, c.gender, c.unionid, c.external_profile, c.created_at, c.updated_at, c.has_delete, c.is_deleted_staff from wx_customer c left join wx_customer_staff cs on cs.customer_id = c.id and cs.has_delete is not null left join wx_customer_staff_tag t on cs.ext_customer_id = t.ext_customer_id and t.ext_staff_id = cs.ext_staff_id and t.has_delete is not null where c.has_delete is not null and (c.is_deleted_staff is null or c.is_deleted_staff = false) and c.has_friend = 1
and c.ext_corp_id = #{dto.extCorpId}
and c.name like concat('%',#{dto.name},'%')
/*如果是查询权限数据，且不是管理员，只能看见自己的数据*/
and cs.ext_staff_id = #{dto.loginStaffExtId}
and cs.ext_staff_id != #{dto.loginStaffExtId}
and t.ext_tag_id in
#{item}
and c.created_at >= #{dto.createTimeBegin}
and c.created_at <= #{dto.createTimeEnd}
group by c.id order by c.created_at desc
